// src/middleware/authMiddleware.js
const authMiddleware = (req, res, next) => {
    // Exemplo de condição de autenticação, substitua conforme necessário
    const isAuthenticated = true; // ou alguma lógica real de autenticação
  
    if (!isAuthenticated) {
      return res.status(401).json({ error: 'Não autorizado' });
    }
    next();
  };
  
  module.exports = authMiddleware;
  
  