// src/models/Event.js
const mongoose = require('mongoose');

const eventSchema = new mongoose.Schema({
  title: String,
  description: String,
  type: String, // Pode ser 'aula', 'palestra', 'evento', 'pessoal', etc.
  date: Date,
});

const Event = mongoose.model('Event', eventSchema);

module.exports = Event;
