// src/routes/events.js
const express = require('express');
const router = express.Router();
const authMiddleware = require('../middleware/authMiddleware');
const eventsController = require('../controllers/eventsController');

// Rota para obter todos os eventos
router.get('/', authMiddleware, eventsController.getAllEvents);

// Rota para obter eventos de um mês específico
router.get('/month/:month', authMiddleware, eventsController.getEventsByMonth);

// Rota para criar um novo evento
router.post('/', authMiddleware, eventsController.createEvent);

// Rota para adicionar nota em qualquer dia do calendário
router.post('/add-note/:eventId', authMiddleware, eventsController.addNote);

// Rota para excluir nota em qualquer dia do calendário
router.delete('/delete-note/:eventId/:noteId', authMiddleware, eventsController.deleteNote);

module.exports = router;
