// test/events.test.js
const request = require('supertest');
const app = require('../src/app');

describe('GET /api/events', () => {
  it('responds with JSON', (done) => {
    request(app)
      .get('/api/events')
      .set('Accept', 'application/json')
      .expect('Content-Type', /json/)
      .expect(200, done);
  });
});
